    //Bitmap32(64 * 3, 64).context2d {
    NativeImage(256, 512).context2d {
        for (n in 0 until 4) {
            drawImage(buildDefaultButton(UiSkinType(n)), n * 64, 0)
        }
        for (n in 0 until 5) {
